import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

}
<nav>
  <a href="/" ><b>HOME</b></a>
  <a href="#container"><b>ABOUT</b></a>
  <a routerLink="/menu" [routerLinkActive]="'active'"><b>MENU</b></a>
  <a routerLink="/cart" [routerLinkActive]="'active'"><b>CART</b></a>
  <a href="#contact"><b>CONTACT</b></a>
  <a routerLink="/firstpage" [routerLinkActive]="'active'"><b>SIGNOUT</b></a>
  <a routerLink="/book" [routerLinkActive]="'active'" class="book-table-button"><b>BOOK A TABLE</b></a>
   </nav>
