import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CartService } from '../../services/cart.service';
import { CartItem, MenuItem } from '../../model';
import { MenuService } from '../../services/menu.service';


@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {

  constructor(private router: Router ,
     private cartService: CartService,
     private menuService: MenuService) 
     {
      this.menuItems = this.menuService.getMenuItems();
      this.categories = this.menuService.getMenuCategories();

    }
  menuItems: MenuItem[] = [];
  filteredMenuItems: MenuItem[] = [];
  categories: string[] = [];
  searchTerm: string = '';


  get combinedMenuItems(): MenuItem[] {
    return this.filteredMenuItems.length > 0 ? this.filteredMenuItems : this.menuItems;
  }

  filterMenuItems(category: string, searchTerm: string): void {
    if (category === 'all') {
      this.filteredMenuItems = this.menuItems;
    } else {
      this.filteredMenuItems = this.menuItems.filter(item => item.category === category);
    }

    if (searchTerm) {
      this.filteredMenuItems = this.filteredMenuItems.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }
  }

  addToCart(item: MenuItem): void {
    const cartItem: CartItem = {
      item: item,
      quantity: 1,
      price: item.price
    };
    this.cartService.addToCart(cartItem);

    this.router.navigateByUrl('/cart'); // Redirect to the cart page
  }
  rateItem(item: MenuItem, rating: number): void {
    item.rating = rating;
  }

}
export { MenuItem };

<div class="menu-container">

  <div class="menu-details" id="menu">
    <h2 class="menu-heading">Menu</h2>
    <div class="menu-categories">
      <div class="search-container">
        <input type="text" placeholder="Search" [(ngModel)]="searchTerm" (input)="filterMenuItems('all', searchTerm)" class="search-bar">

      </div>
      <button class="category-button" (click)="filterMenuItems('all', searchTerm)">All</button>
      <button class="category-button" *ngFor="let category of categories" (click)="filterMenuItems(category, searchTerm)">{{ category }}</button>

    </div>
    <div class="menu-items">
      <div class="menu-item" *ngFor="let item of combinedMenuItems">
        <img [src]="item.image" alt="{{ item.name }}" class="item-image">
      <h3>{{ item.name }}</h3>
      <p>{{ item.description }}</p>
      <p class="item-price">{{ item.price | currency }}</p>

      <button (click)="addToCart(item)">Add to Cart</button>
    </div>
  </div> 
 </div>
</div>
